$(document).on('click', '.removeInfo', function(e) {
    e.preventDefault();
    vinculo = $(this).attr("data-url");
    respuesta = $(this).attr("data-response");
    alertify.confirm('<h4 class="text-bold">Are you sure you want to continue?</h4><p>Remember that the removal is permanent.</p>')
        .set('title', '<span class="text-warning"><i class="fa fa-exclamation-triangle"></i> Warning</span>')
        .set('labels', {ok: 'Continue', cancel: 'Cancel'})
        .set('onok', function() {
            $.blockUI({css: {border: 'none', overflow: 'hidden !important', padding: '15px', backgroundColor: '#000', '-webkit-border-radius': '10px', '-moz-border-radius': '10px', opacity: .5, color: '#fff'}});
            $.post(getUrl+vinculo, 
            { }, 
            function (response) {
                alertify.closeAll();
                $.unblockUI({});
                $('.'+respuesta).html(response);
            });
            return false;
        })
        .set('oncancel', function(){
            alertify.error("The process was canceled.");
        });
});

