$("#checkTodo").change(function () {
    if ($(this).is(':checked')) {
        //$("input[type=checkbox]").prop('checked', true); //todos los check
        $("#infoTable input[type=checkbox]").prop('checked', true);
    } else {
        //$("input[type=checkbox]").prop('checked', false);//todos los check
        $("#infoTable input[type=checkbox]").prop('checked', false);
    }
});

/* ------------------------------- */

function init_countTa(idtextarea, idcontador, max){
    $(document).ready(function(){
        $("#"+idtextarea).keyup(function(){
            updateContadorTa(idtextarea, idcontador, max);
        });
        $("#"+idtextarea).change(function(){
            updateContadorTa(idtextarea, idcontador, max);
        });
    });
}

function updateCountTa(idtextarea, idcontador,max){
    $(document).ready(function(){
        var contador = $("#"+idcontador);
        var ta = $("#"+idtextarea);
        contador.html("0/"+max);
        contador.html(ta.val().length+"/"+max);
        if(parseInt(ta.val().length)>max){
            ta.val(ta.val().substring(0,max-1));
            contador.html(max+"/"+max);
        }
    });
}
